'use strict';

var msgpack      = require('msgpack');
var dateUnpacker = require('./date_unpacker');

function Unpacker(obj) {
  var unpacked = msgpack.unpack(obj);

  if(typeof unpacked !== 'object') {
    return unpacked;
  }

  return deepUnpack(unpacked);
}

function deepUnpack(obj) {
  if(typeof obj !== 'object' || obj === null) {
    return obj;
  } else if(dateUnpacker.isDate(obj)) {
    return dateUnpacker.unpack(obj);
  } else {
    for(var i in obj) {
      obj[i] = deepUnpack(obj[i]);
    }

    return obj;
  }
}

module.exports.Unpacker = Unpacker;
